function K8NO8Q63Z3B9RB1DU289GU2T03D0S09E2HP71QP4K9SSGUM2D013E6F03F09DCC67B41105625283B0CF7174(id,fetchMethod){
  var cfetchMothod = document.getElementById("fetchMothod"+id).value;
  if(cfetchMothod==fetchMethod)return;

  var btn = jQuery('#changebnt'+id);
  var btnName = btn.html();

  jQuery.ajax({
     type: "GET",
     url: "<?php echo $EU71J15600NE939EEL5438VZ2311SE054KFE763558UP359MDSI0V6A64LL819T0D8AA6C1951E8DB2FD2403CE0191526C16574927; ?>",
